  #{ifaceAId,jdbcType=BIGINT}, #{ifaceBName,jdbcType=VARCHAR}, #{ifaceBId,jdbcType=BIGINT}, 
  #{direction,jdbcType=TINYINT}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

