  #{description,jdbcType=VARCHAR}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(0) from entity_org_structure


		and name = #{search,jdbcType=VARCHAR} 


		and parent_id = #{parentId,jdbcType=BIGINT} 


		and org_level = #{orgLevel,jdbcType=INTEGER}

select from entity_org_structure
name = #{search,jdbcType=VARCHAR}
order by gmt_create desc, id desc

