mapper.select:
  	select count(0) from alarm_info


			and notice_rank = #{noticeRank,jdbcType=TINYINT} 


			and handle_state = #{handleState,jdbcType=TINYINT} 


			and handle_user = #{handleUser,jdbcType=VARCHAR} 


  			and device_name = #{deviceName,jdbcType=VARCHAR} 


  			and channel_name = #{channelName,jdbcType=VARCHAR} 


			and type in 

        		${alarmType}  



			AND record_time  >=  #{startDate,jdbcType=VARCHAR}


			AND record_time  <=  #{endDate,jdbcType=VARCHAR}

  	select 
  	from alarm_info


			and notice_rank = #{noticeRank,jdbcType=TINYINT} 


			and handle_state = #{handleState,jdbcType=TINYINT} 


			and handle_user = #{handleUser,jdbcType=VARCHAR} 


  			and device_name = #{deviceName,jdbcType=VARCHAR} 


  			and channel_name = #{channelName,jdbcType=VARCHAR} 


			and type in 

        		${alarmType}  



			AND record_time  >=  #{startDate,jdbcType=VARCHAR}


			AND record_time  <=  #{endDate,jdbcType=VARCHAR}


  	order by gmt_create desc, id desc

mapper.update:
update alarm_info
set handle_state = #{handleState,jdbcType=TINYINT},
  handle_msg = #{handleMsg,jdbcType=VARCHAR},
  handle_user = #{handleUser,jdbcType=VARCHAR},
  gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
  	select DATE_FORMAT(record_time, '%Y-%m-%d %H:00:00') AS stat_time, COUNT(*) AS total, SUM(`count`) AS `count` 
  	from alarm_info
  	where DATE_FORMAT(record_time, '%Y%m%d') = #{curDay,jdbcType=VARCHAR} 

		and type in 

       		${alarmType}  


	 and channel_name = #{channelName}
	GROUP BY stat_time 
  	ORDER BY stat_time

  	select DATE_FORMAT(record_time, '%Y-%m-%d 00:00:00') AS stat_time, COUNT(*) AS total, SUM(`count`) AS `count` 
  	from alarm_info
  	where DATE_FORMAT(record_time, '%Y%m%d')  >=  #{startDay,jdbcType=VARCHAR} 
  		and DATE_FORMAT(record_time, '%Y%m%d')  <=  #{endDay,jdbcType=VARCHAR} 

		and type in 

       		${alarmType}  


	 and channel_name = #{channelName}
	GROUP BY stat_time 
  	ORDER BY stat_time

  	select ${fieldName}, COUNT(*) AS total, SUM(`count`) AS `count` 
  	from alarm_info
  	where DATE_FORMAT(record_time, '%Y%m%d')  >=  #{startDay,jdbcType=VARCHAR} 
  		and DATE_FORMAT(record_time, '%Y%m%d')  <=  #{endDay,jdbcType=VARCHAR} 

		and type in 

       		${alarmType}  


	 and channel_name = #{channelName}
	GROUP BY ${fieldName} 
  	ORDER BY ${orderName} DESC
