mapper.select:
update user
set enabled = abs(enabled - 1)
where name = #{name,jdbcType=VARCHAR};
select 

from user
where name = #{name,jdbcType=VARCHAR}

select u.* FROM user u INNER JOIN user_permission_group upg ON u.id = upg.user_id INNER JOIN permission_group pg ON upg.permission_group_id = pg.id where pg.type=#{clientUserType,jdbcType=BIT}
and u.name like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')
and u.enabled=#{enabled, jdbcType=BIT}

select count(1) FROM user u INNER JOIN user_permission_group upg ON u.id = upg.user_id INNER JOIN permission_group pg ON upg.permission_group_id = pg.id where pg.type=#{clientUserType,jdbcType=BIT}
and u.name like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')
and u.name != #{approvalUserName,jdbcType=VARCHAR}
