mapper.select:
select 
from business_device_basic
where business_visit_id = #{businessVisitId,jdbcType=BIGINT}

	and (waf_work_state = #{workState,jdbcType=TINYINT}
		or virus_work_state = #{workState,jdbcType=TINYINT}
		or work_state = #{workState,jdbcType=TINYINT})


	and deleting = #{deleting,jdbcType=TINYINT}

select 
from business_device_basic
where channel_info_id = #{deviceId,jdbcType=BIGINT}

	and work_state = #{workState,jdbcType=TINYINT}

select count(0)
from business_device_basic
where business_visit_id = #{businessVisitId,jdbcType=BIGINT}

	and (waf_work_state = #{workState,jdbcType=TINYINT}
		or virus_work_state = #{workState,jdbcType=TINYINT}
		or work_state = #{workState,jdbcType=TINYINT})


	and deleting = #{deleting,jdbcType=TINYINT}

select b.* 
from business_device_basic b JOIN business_visit v ON b.business_visit_id = v.id 
where b.channel_info_id = #{deviceId,jdbcType=BIGINT} 
and v.l4_proto = #{l4proto,jdbcType=VARCHAR}

  select b.* 
  from business_device_basic b JOIN business_visit v ON b.business_visit_id = v.id 
  where b.channel_info_id = #{deviceId,jdbcType=BIGINT} 
  and v.l7_proto = #{l7proto,jdbcType=VARCHAR}

		and v.l7_proto = #{l7proto,jdbcType=VARCHAR}


		and v.l7_proto != ''
