mapper.insert:

  SELECT LAST_INSERT_ID()

insert into business_strategy (business_device_basic_id, app_type, type, strategy_id, 
  iface_id, work_state, channel_info_id, deleting, gmt_create, gmt_modified
  )
values (#{businessDeviceBasicId,jdbcType=BIGINT}, #{appType,jdbcType=TINYINT}, #{type,jdbcType=TINYINT}, 
  #{strategyId,jdbcType=BIGINT}, #{ifaceId,jdbcType=BIGINT}, #{workState,jdbcType=TINYINT}, #{channelInfoId,jdbcType=BIGINT}, 
  #{deleting,jdbcType=BIT}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

mapper.select:
select 
from business_strategy
where business_device_basic_id = #{businessDeviceBasicId,jdbcType=BIGINT} 
and type = #{type,jdbcType=TINYINT} and strategy_id = #{strategyId,jdbcType=BIGINT}

    and app_type = #{appType,jdbcType=TINYINT}

	select 
  from business_strategy
  where business_device_basic_id = #{businessDeviceBasicId,jdbcType=BIGINT}

  	and work_state = #{workState,jdbcType=TINYINT}


  	and deleting = #{deleting,jdbcType=TINYINT}


      and app_type = #{appType,jdbcType=TINYINT}

	select count(0)
  from business_strategy
  where business_device_basic_id = #{businessDeviceBasicId,jdbcType=BIGINT}

  	and work_state = #{workState,jdbcType=TINYINT}


  	and deleting = #{deleting,jdbcType=TINYINT}


      and app_type = #{appType,jdbcType=TINYINT}

mapper.delete:
delete from business_strategy
where business_device_basic_id = #{businessDeviceBasicId,jdbcType=BIGINT} 
and work_state = #{workState,jdbcType=TINYINT}

    and app_type = #{appType,jdbcType=TINYINT}

mapper.select:
	select 
  from business_strategy
  where strategy_id = #{strategyId,jdbcType=BIGINT}

    	and type = #{type,jdbcType=TINYINT}


  	and deleting = #{deleting,jdbcType=TINYINT}


      and app_type = #{appType,jdbcType=TINYINT}

	select 
  from business_strategy
  where business_device_basic_id = #{businessDeviceBasicId,jdbcType=BIGINT}

    	and type = #{type,jdbcType=TINYINT}

	select 
  from business_strategy
  where channel_info_id = #{channelInfoId,jdbcType=BIGINT} 

  	and type = #{type,jdbcType=TINYINT} 


  	and strategy_id = #{strategyId,jdbcType=BIGINT} 

  limit 1

mapper.delete:
delete from business_strategy
where channel_info_id = #{channelInfoId,jdbcType=BIGINT} 

    and type = #{type,jdbcType=TINYINT}
