mapper.select:
  	select count(0) from baseline_info 


			and type = #{type,jdbcType=VARCHAR} 


  			and channel_id = #{channelId,jdbcType=BIGINT} 


  			and ip = #{ip,jdbcType=VARCHAR} 


  			and result = #{result,jdbcType=VARCHAR} 


			AND record_time  >=  #{startDate,jdbcType=VARCHAR}


			AND record_time  <=  #{endDate,jdbcType=VARCHAR}

  	select 
  	from baseline_info


			and type = #{type,jdbcType=VARCHAR} 


  			and channel_id = #{channelId,jdbcType=BIGINT} 


  			and ip = #{ip,jdbcType=VARCHAR} 


  			and result = #{result,jdbcType=VARCHAR} 


			AND record_time  >=  #{startDate,jdbcType=VARCHAR}


			AND record_time  <=  #{endDate,jdbcType=VARCHAR}


  	order by gmt_create desc, id desc

mapper.update:
  update baseline_info
  set type = 'history' 


			and channel_id = #{channelId,jdbcType=BIGINT} 


			and ip = #{ip,jdbcType=VARCHAR}
