mapper.select:
select count(0) from channel_diagnose_tool


		and command like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')


		and command = #{command,jdbcType=VARCHAR}


		and params like CONCAT('%', #{params,jdbcType=VARCHAR}, '%')


		and device_id = #{deviceId,jdbcType=BIGINT} 


		and channel_info_id = #{channelInfoId,jdbcType=BIGINT}

select 
from channel_diagnose_tool


		and command like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')


		and command = #{command,jdbcType=VARCHAR}


		and params like CONCAT('%', #{params,jdbcType=VARCHAR}, '%')


		and device_id = #{deviceId,jdbcType=BIGINT} 


		and channel_info_id = #{channelInfoId,jdbcType=BIGINT} 


order by gmt_create desc, id desc
