mapper.insert:

  SELECT LAST_INSERT_ID()

insert into channel_ha_device (channel_ha_id, channel_info_id, 
  device_number, device_name, device_role, enable, device_ha_ip, work_state, work_status, 
  gmt_create, gmt_modified
  )
values (#{channelHaId,jdbcType=BIGINT}, #{channelInfoId,jdbcType=BIGINT}, 
  #{deviceNumber,jdbcType=INTEGER}, #{deviceName,jdbcType=VARCHAR}, #{deviceRole,jdbcType=TINYINT}, 
  #{enable,jdbcType=BIT}, #{deviceHaIp,jdbcType=VARCHAR}, #{workState,jdbcType=TINYINT}, 
  #{workStatus,jdbcType=VARCHAR}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

mapper.select:
select 
from channel_ha_device
where channel_info_id = #{channelInfoId,jdbcType=BIGINT} 

    and channel_ha_id != #{channelHaId,jdbcType=BIGINT}

select 
from channel_ha_device
where channel_ha_id = #{channelHaId,jdbcType=BIGINT} 

	and work_status = #{workStatus,jdbcType=TINYINT}


	and device_role = #{deviceRole,jdbcType=TINYINT}

limit 1

mapper.update:
update channel_ha_device
set enable = #{enable,jdbcType=BIT} 
where channel_ha_id = #{channelHaId,jdbcType=BIGINT}

update channel_ha_device
set work_status = #{workStatus,jdbcType=TINYINT} 
where channel_ha_id = #{channelHaId,jdbcType=BIGINT}
