mapper.select:
	select count(0) from channel_info


			and name like CONCAT('%', #{search,jdbcType=VARCHAR}, '%') 


          and device_id = #{deviceId,jdbcType=TINYINT} 


          and id = #{id,jdbcType=BIGINT}

	select 
	from channel_info


          and channel_type = #{channelType,jdbcType=TINYINT}


          and device_sub_type = #{deviceSubType,jdbcType=TINYINT}


			and name like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')


          and device_id = #{deviceId,jdbcType=TINYINT} 


          and id = #{id,jdbcType=BIGINT} 


	order by gmt_create desc, id desc

update channel_info
set baseline_check = abs(baseline_check - 1)
where id = #{id,jdbcType=BIGINT};
