mapper.select:
select count(0) from channel_router

	1 = 1

		and destination_ip like CONCAT('%', #{search,jdbcType=VARCHAR}, '%') 


		and device_id = #{deviceId,jdbcType=BIGINT} 


		and channel_info_id = #{channelInfoId,jdbcType=BIGINT}

select from channel_router
1 = 1
and destination_ip like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')
and device_id = #{deviceId,jdbcType=BIGINT}
and channel_info_id = #{channelInfoId,jdbcType=BIGINT}
order by gmt_create desc, id desc

	select 
  from channel_router
  where channel_info_id = #{channelInfoId,jdbcType=BIGINT}
  and destination_ip = #{destinationIp,jdbcType=VARCHAR}

		and work_area = #{workArea,jdbcType=VARCHAR} 

	limit 1
