mapper.insert:

  SELECT LAST_INSERT_ID()

insert into entity_access_device (name, ip, operate_system,
  org_structure_id, org_structure_name, sys_client_id,confidentiality_level,
  gmt_create, gmt_modified
)
values (#{name,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR},  #{operateSystem,jdbcType=VARCHAR},
  #{orgStructureId,jdbcType=VARCHAR}, #{orgStructureName,jdbcType=VARCHAR}, #{sysClientId,jdbcType=BIGINT}, #{confidentialityLevel,jdbcType=TINYINT},
  #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
)

mapper.select:
	select count(0) from entity_access_device


			and ip like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')


			and name like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


			and ip like CONCAT('%', #{ip,jdbcType=VARCHAR}, '%')


			and org_structure_name = #{signScope,jdbcType=VARCHAR}


        and confidentiality_level = #{confidentialityLevel,jdbcType=TINYINT}
