mapper.select:
select

from user_entity_access_device
where user_id = #{userId,jdbcType=BIGINT} AND entity_access_device_id=#{entityId,jdbcType=BIGINT}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into user_entity_access_device (entity_access_device_id, user_id, approve_user_id, active_status,
                                      gmt_create, gmt_modified, login_ip, gmt_login)
values (#{entityAccessDeviceId,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{approveUserId,jdbcType=BIGINT}, #{activeStatus,jdbcType=BIT},
        #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP},#{loginIp,jdbcType=VARCHAR}, #{gmtLogin,jdbcType=TIMESTAMP}
      )
