mapper.insert:

  SELECT LAST_INSERT_ID()

insert into init_interface (name, mac, device_id, external, ips, type, if_type, status, 
  `row`, `column`, info, gmt_create, gmt_modified
  )
values (#{name,jdbcType=VARCHAR}, #{mac,jdbcType=VARCHAR}, #{deviceId,jdbcType=BIGINT}, #{external,jdbcType=TINYINT}, 
  #{ips,jdbcType=VARCHAR}, #{type,jdbcType=TINYINT}, #{ifType,jdbcType=TINYINT}, #{status,jdbcType=TINYINT}, 
  #{row,jdbcType=TINYINT}, #{column,jdbcType=TINYINT}, #{info,jdbcType=VARCHAR}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

mapper.select:
select 

from init_interface
where device_id = #{deviceId,jdbcType=BIGINT}
and `row` = #{row,jdbcType=TINYINT}
and `column` = #{column,jdbcType=TINYINT}
limit 1

select 

from init_interface
where device_id = #{deviceId,jdbcType=BIGINT}
    and name = #{name,jdbcType=VARCHAR} 

	and type = #{type,jdbcType=TINYINT}

limit 1

select count(0) from init_interface
where device_id = #{deviceId,jdbcType=BIGINT}
    and name = #{name,jdbcType=VARCHAR}
