mapper.select:
select count(0) from network_resource


		and name like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


		and iface_id = #{ifaceId,jdbcType=BIGINT}


		and ip like CONCAT('%', #{ip,jdbcType=VARCHAR}, '%')


		and user_name like CONCAT('%', #{userName,jdbcType=VARCHAR}, '%')


		and work_state = #{workState,jdbcType=TINYINT}


		and channel_state = #{channelState,jdbcType=TINYINT}

select 
from network_resource


		and name like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


		and iface_id = #{ifaceId,jdbcType=BIGINT}


		and ip like CONCAT('%', #{ip,jdbcType=VARCHAR}, '%')


		and user_name like CONCAT('%', #{userName,jdbcType=VARCHAR}, '%')


		and work_state = #{workState,jdbcType=TINYINT}


		and channel_state = #{channelState,jdbcType=TINYINT}


order by gmt_create desc, id desc
