mapper.select:
select 
from strategy_area_mark


		and device_type = #{deviceType,jdbcType=TINYINT}


		and location = #{location,jdbcType=TINYINT}

select 
from strategy_area_mark


		strategy_mark_id = #{markId,jdbcType=BIGINT} 
		or transfer_strategy_mark_id = #{markId,jdbcType=BIGINT} 
		or session_reverse_strategy_mark_id = #{markId,jdbcType=BIGINT} 
		or session_reverse_transfer_strategy_mark_id = #{markId,jdbcType=BIGINT}

select 
from strategy_area_mark 
where source_network_area_id = #{networkAreaId,jdbcType=BIGINT} 
or destination_network_area_id = #{networkAreaId,jdbcType=BIGINT}
