mapper.select:
	select 
	from strategy_content where match_type = #{matchType,jdbcType=VARCHAR}
	and keywords_list_id = #{keywordsListId,jdbcType=BIGINT} 

  	and exec_action = #{execAction,jdbcType=TINYINT}

	select 
	from strategy_content where 1=1 

  	and exec_action = #{execAction,jdbcType=TINYINT}
