mapper.select:
  	select 
  		from operation_log


				and FIND_IN_SET(operation_type, #{operationTypes,jdbcType=VARCHAR})


				and ${fieldName} like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')


				and operation_username = #{operationUsername,jdbcType=VARCHAR} 


				and permission_group_name = #{permissionGroupName,jdbcType=VARCHAR} 


				and ip = #{ip,jdbcType=VARCHAR} 


				and module like CONCAT('%', #{module,jdbcType=VARCHAR}, '%')


				and state = #{state,jdbcType=BIT} 


				AND gmt_create  >=  #{startDate,jdbcType=VARCHAR}


				AND gmt_create  <=  #{endDate,jdbcType=VARCHAR}


				AND permission_group_name IN (SELECT name FROM permission_group WHERE `type` != 4) 


				AND (permission_group_name IN (SELECT name FROM permission_group WHERE `type` = 4) 
					or operation_username = 'timer')


		order by gmt_create desc, id desc

  	select count(0) from operation_log


				and FIND_IN_SET(operation_type, #{operationTypes,jdbcType=VARCHAR})


				and ${fieldName} like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')


				and operation_username = #{operationUsername,jdbcType=VARCHAR} 


				and permission_group_name = #{permissionGroupName,jdbcType=VARCHAR} 


				and ip = #{ip,jdbcType=VARCHAR} 


				and module like CONCAT('%', #{module,jdbcType=VARCHAR}, '%')


				and state = #{state,jdbcType=BIT} 


				AND gmt_create  >=  #{startDate,jdbcType=VARCHAR}


				AND gmt_create  <=  #{endDate,jdbcType=VARCHAR}


				AND permission_group_name IN (SELECT name FROM permission_group WHERE `type` != 4) 


				AND (permission_group_name IN (SELECT name FROM permission_group WHERE `type` = 4) 
					or operation_username = 'timer')

mapper.delete:
  	delete from operation_log


				and FIND_IN_SET(operation_type, #{operationTypes,jdbcType=VARCHAR})


				and ${fieldName} like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')


				AND gmt_create  >=  #{startDate,jdbcType=VARCHAR}


				AND gmt_create  <=  #{endDate,jdbcType=VARCHAR}

mapper.select:
select 
from operation_log


		and FIND_IN_SET(operation_type, #{operationTypes,jdbcType=VARCHAR}


		AND str_create  >=  #{startDate,jdbcType=VARCHAR}


