mapper.insert:

  SELECT LAST_INSERT_ID()

insert into trans_data (type, name, network_area_id, network_area_name, 
  transmit_type, ip, port, gmt_create, gmt_modified
  )
values (#{type,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{networkAreaId,jdbcType=BIGINT}, 
  #{networkAreaName,jdbcType=VARCHAR}, #{transmitType,jdbcType=VARCHAR}, 
  #{ip,jdbcType=VARCHAR}, #{port,jdbcType=INTEGER}, 
  #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(0) from trans_data


		and name like CONCAT('%', #{search,jdbcType=VARCHAR}, '%')


		and type = #{type,jdbcType=VARCHAR}
