CREATE TABLE `baseline_info`(`id` BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'ID',`type` VARCHAR(20) COMMENT '记录类型',`blsec_ip` VARCHAR(50) COMMENT '通道IP',`channel_id` BIGINT(20) UNSIGNED COMMENT '通道ID',`channel_name` VARCHAR(255) COMMENT '通道名称',`device_name` VARCHAR(255) COMMENT '设备名称',`ip` VARCHAR(50) COMMENT '客户端IP',`filename` VARCHAR(500) COMMENT '结果文件名称',`result` VARCHAR(10) COMMENT '结果',`score` INT(11) COMMENT '分数',`total` INT(11) COMMENT '总项数',`success` INT(11) COMMENT '成功项数',`fail` INT(11) COMMENT '失败项数',`record_time` DATETIME COMMENT '记录时间',`gmt_create` DATETIME,`gmt_modified` DATETIME,PRIMARY KEY (`id`)) ENGINE=INNODB CHARSET=utf8 COLLATE=utf8_general_ci;